package VARIBALE;

public class VariablePrgm {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String MachineName;
		
		MachineName = "Rivetting Machine";
		System.out.println(MachineName);
		int MachineNumber;
		MachineNumber =10;
		System.out.println(MachineNumber);
		char grade;
		grade = 'A';
		System.out.println(grade);
		System.out.println("Machine Name is "  + MachineName);		
		System.out.println("Machine Number is "  + MachineNumber);
		System.out.println("Machine Grade is "  + grade);
		
				
		 
		

		
		

	}

}
